import { Component } from '@angular/core';

@Component({
    selector: 'app-footer',
    templateUrl: './footer.component.html',
    styleUrls: ['./footer.component.css']
})
export class FooterComponent {
    currentYear = new Date().getFullYear();

    socialLinks = [
        { name: 'GitHub', icon: 'üêô', url: 'https://github.com/lestrange41' },
        { name: 'LinkedIn', icon: 'üíº', url: 'https://linkedin.com/in/joelmontilla' },
        { name: 'Email', icon: 'üìß', url: 'mailto:joelmontillaher@gmail.com' }
    ];
}
