import { Component } from '@angular/core';

interface Skill {
    name: string;
    level: number;
    icon: string;
}

@Component({
    selector: 'app-skills',
    templateUrl: './skills.component.html',
    styleUrls: ['./skills.component.css']
})
export class SkillsComponent {
    skills: Skill[] = [
        { name: 'Angular', level: 95, icon: 'üÖ∞Ô∏è' },
        { name: 'TypeScript', level: 90, icon: 'üìò' },
        { name: 'JavaScript', level: 90, icon: 'üìú' },
        { name: 'React.js', level: 85, icon: '‚öõÔ∏è' },
        { name: 'Node.js', level: 85, icon: 'üü¢' },
        { name: 'HTML5 & CSS3', level: 95, icon: 'üåê' },
        { name: 'Git', level: 88, icon: 'üîß' },
        { name: 'Docker', level: 75, icon: 'üê≥' },
        { name: 'REST APIs', level: 90, icon: 'üîå' },
        { name: 'RxJS', level: 85, icon: 'üîÑ' },
        { name: 'Next.js', level: 80, icon: '‚ñ≤' },

    ];
}
